// Prefix Featurse with Prerequisites
// ==================================
// Updates the selected token's character sheet so that the Features on it are prefixed with the prerequisites for that
// feature.  For example, a fighter's first-level feature "Second Wind" would become "[Fighter 1] Second Wind".  Ignores
// any features that already start with a "[" or doesn't have any requirements.
//
// To use:  select a single token that you have rights to update, and then click "Execute Macro"

if (canvas.tokens.controlled.length == 0) { ui.notifications.error("You must select a token first."); }
if (canvas.tokens.controlled.length > 1)  { ui.notifications.error("You must only select a single token."); }

let selectedToken = canvas.tokens.controlled[0];
let actor = selectedToken.actor;
// check permission
if (actor.ownership.default == 3 || actor.ownership[game.user._id] == 3) {
    let updates = actor.items
    		.filter( x => x.type == 'feat' )
    		.filter( x => x.system?.requirements != "" )
    		.filter( x => !x.name.startsWith("[") )
    		.reduce((acc, item) => {
    			acc.push({ _id: item.id, "name": `[${item.system.requirements}] ${item.name}` });
    			return acc;
    		 }, []);
    ui.notifications.info(`Updating ${updates.length} features on ${actor.name}`);
    await actor.updateEmbeddedDocuments("Item", updates);
    ui.notifications.info(`Features on ${actor.name} successfully updated.`);
} else {
    ui.notifications.error("You do not have permissions to update this token.");
}
